marked.setOptions({headerIds:false});function readFile(file,out){var http=new XMLHttpRequest();http.open('get',file);http.onreadystatechange=function(){document.getElementById(out).innerHTML=marked(http.responseText)};http.send()}function readTime(tpath,file){var http=new XMLHttpRequest();http.open('get',tpath+file+".md",false);http.onload=function(){var mark=marked(http.responseText);var content=document.getElementById("content");var newNode=document.createElement('div');newNode.innerHTML=mark;newNode.setAttribute('id',file);content.append(newNode);};http.send();}